body.raw:
{
    "subject_exercises": "Parasitologia",
    "description_exercises": "Exercícios para revisão de ectoparasitas como pulgas, carrapatos e piolhos, abordando ciclo de vida, transmissão de doenças, identificação e controle.",
    "grade_level_exercises": "Ensino Superior",
    "complexity_level_exercises": "Avançado",
    "duration_minutes_exercises": 90,
    "objectives_exercises": [
        {
            "titleObjectiveExercises": "Identificar ectoparasitas",
            "contentObjectiveExercises": "O aluno deverá reconhecer características morfológicas e biológicas de pulgas, carrapatos e piolhos."
        },
        {
            "titleObjectiveExercises": "Compreender transmissão de doenças",
            "contentObjectiveExercises": "O aluno deverá entender como esses ectoparasitas atuam como vetores de doenças e os fatores que influenciam a infestação."
        },
        {
            "titleObjectiveExercises": "Aplicar métodos de controle",
            "contentObjectiveExercises": "O aluno deverá avaliar técnicas de prevenção, tratamento e manejo ambiental para controlar infestações."
        }
    ],
    "themes_exercises": [
        {
            "titleThemeExercises": "Pulgas",
            "contentThemeExercises": "Estudo do ciclo de vida, hospedagem, transmissão de patógenos e medidas de controle."
        },
        {
            "titleThemeExercises": "Carrapatos",
            "contentThemeExercises": "Exploração de espécies de carrapatos, doenças transmitidas e prevenção em animais domésticos."
        },
        {
            "titleThemeExercises": "Piolhos",
            "contentThemeExercises": "Análise de diferentes tipos de piolhos, sua morfologia, ciclo de vida e estratégias de combate."
        }
    ],
    "exercises": [
        {
            "type_exercise": "multipla-escolha",
            "title_exercise": "Identificação de pulgas e seu papel na transmissão de doenças",
            "content_exercise": "As pulgas são ectoparasitas hematófagos que podem transmitir diversas doenças. A identificação correta é crucial para o controle e prevenção. Considerando as características morfológicas e biológicas das pulgas, assinale a opção correta.",
            "options_exercise_multipla_escolha": [
                {
                    "option": "A",
                    "content_option": "A presença de ctenídeos genais e pronotais é exclusiva de todas as espécies de pulgas, facilitando a identificação imediata."
                },
                {
                    "option": "B",
                    "content_option": "A capacidade de saltar é um fator irrelevante na dispersão de pulgas, já que a maioria se locomove rastejando sobre o hospedeiro."
                },
                {
                    "option": "C",
                    "content_option": "A transmissão da peste bubônica é exclusivamente realizada pela pulga do rato (Xenopsylla cheopis) em ambientes urbanos e rurais."
                },
                {
                    "option": "D",
                    "content_option": "O ciclo de vida das pulgas é completo, envolvendo ovo, larva, pupa e adulto, sendo que as larvas se alimentam exclusivamente de sangue."
                },
                {
                    "option": "E",
                    "content_option": "A identificação de pulgas é baseada na análise das cerdas, espinhos e outros apêndices do corpo, permitindo diferenciar espécies vetoras de doenças."
                }
            ],
            "correct_answer_exercise": "E",
            "explanation_exercise": "A identificação correta das pulgas envolve a análise detalhada de suas estruturas morfológicas, como cerdas, espinhos e outros apêndices. Essa análise é fundamental para diferenciar espécies vetoras de doenças e implementar medidas de controle eficazes.",
            "bloom_level": "analisar"
        },
        {
            "type_exercise": "discursiva",
            "title_exercise": "Avaliação de métodos de controle de carrapatos em animais domésticos",
            "content_exercise": "Diversos métodos são utilizados para controlar infestações de carrapatos em animais domésticos, desde produtos químicos até abordagens de manejo ambiental. Discuta criticamente a eficácia e os riscos associados a cada um desses métodos, considerando a saúde do animal, o impacto ambiental e o desenvolvimento de resistência por parte dos carrapatos.",
            "options_exercise_multipla_escolha": [],
            "options_exercise_verdadeiro_falso": [],
            "correct_answer_exercise": null,
            "explanation_exercise": "Espera-se que o aluno avalie a eficácia e os riscos de diferentes métodos de controle de carrapatos, como acaricidas, coleiras antipulgas, banhos carrapaticidas e controle ambiental. A resposta deve considerar a importância de um manejo integrado, a fim de minimizar a resistência dos carrapatos e os impactos negativos na saúde animal e no meio ambiente.",
            "bloom_level": "avaliar"
        },
        {
            "type_exercise": "verdadeiro-falso",
            "title_exercise": "Características e ciclo de vida dos piolhos",
            "content_exercise": "Analise as seguintes afirmações sobre piolhos e indique se são verdadeiras ou falsas.",
            "options_exercise_verdadeiro_falso": [
                {
                    "option": "V",
                    "content_option": "Os piolhos são ectoparasitas ápteros (sem asas) que se alimentam de sangue ou restos de pele dos hospedeiros."
                },
                {
                    "option": "F",
                    "content_option": "A pediculose é uma infestação causada exclusivamente pelo Pediculus humanus capitis, que afeta apenas o couro cabeludo."
                },
                {
                    "option": "V",
                    "content_option": "As lêndeas são ovos de piolhos firmemente aderidos aos fios de cabelo, e sua remoção é fundamental para o controle da infestação."
                },
                {
                    "option": "F",
                    "content_option": "A transmissão de piolhos ocorre principalmente por meio do contato com animais domésticos infestados."
                },
                {
                    "option": "V",
                    "content_option": "O tratamento da pediculose envolve o uso de produtos pediculicidas e a remoção manual das lêndeas para evitar reinfestações."
                }
            ],
            "correct_answer_exercise": "V, F, V, F, V",
            "explanation_exercise": "1. Verdadeiro: Piolhos são insetos ápteros ectoparasitas. 2. Falso: A pediculose pode ser causada por diferentes espécies de piolhos. 3. Verdadeiro: A remoção das lêndeas é crucial para evitar reinfestações. 4. Falso: A transmissão de piolhos é principalmente entre humanos. 5. Verdadeiro: O tratamento envolve pediculicidas e remoção manual das lêndeas.",
            "bloom_level": "lembrar"
        }
    ]
}

